import { useState } from 'react'

const Button = (props) => (
  <button onClick={props.handleClick}> {props.text} </button>
)

const Display = props => <div> <p> {props.text} {props.value}</p> </div>

const Statistics = (props) => {
  if (props.allClicks.length === 0) {
    return <p>No feedback given</p>
  }
  return (
    <div>
      <p> {props.text} {props.value} </p>
      <p> {props.text2} {props.value2} </p>
      <p> {props.text3} {props.value3} </p>
    </div>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  //const  [avergage, setAverage] = useState(0)

  const setToValueGood = (newValue) => {
    console.log("value now", newValue)
    setGood(newValue)
  }
  const setToValueNeutral = (newValue) => {
    console.log("value now", newValue)
    setNeutral(newValue)
  }

  const setToValueBad= (newValue) => {
    console.log("value now", newValue)
    setBad(newValue)
  }

  return (
    <div>
      <h1>Give Feedback</h1>
      <Button handleClick={() => setToValueGood(good + 1)} text= " Good "/>
      <Button handleClick={() => setToValueNeutral(neutral + 1 )} text= " Neutral "/>
      <Button handleClick={() => setToValueBad(bad + 1)} text= " Bad "/>

      <h1>Statistics</h1>
      <Statistics text="Good" value={good} />
      <Statistics text="Neutral" value={neutral} />
      <Statistics text="Bad" value={bad} />

      <Display text="All" value={good + neutral + bad} />
      <Display text="Average" value={(good - bad) / (good + neutral + bad)} />
      <Display text="Positive" value={good / (good + neutral + bad)} />

    </div>
  )
}

export default App
