  return (
    <div className="App">
      <p>Hola {props.name} tienes {props.age} años</p>
    </div>
  )
}

const App = () => {
  const name = 'Juan'
  const age = 30

  return (
    <div>
      <h1>Saludos!</h1>
      <Hello name="Jorge" age={26+10}/>
      <Hello name="Albero" age={age + 15}/>
      <Hello name={name} age={age}/>
    </div>
  )
}

